mapper.insert:
INSERT INTO 
	user (user_id, user_nickname, user_email, user_phone_number, user_gender, user_img, user_birth, user_token)
VALUES (#{userId}, #{nickName}, #{email}, #{phoneNumber}, #{gender}, #{imgUrl}, #{birth}, #{refreshToken});

mapper.update:
UPDATE user
SET 
    user_token = #{refreshToken}
WHERE 
    user_id = #{userId};
